import React, { Component } from "react";
import { Map, GoogleApiWrapper, InfoWindow, Marker } from "google-maps-react";

const mapStyles = {
  width: "100%",
  height: "100%"
};

export class MapContainer extends Component {
  render() {
    state = {
      showingInfoWindow: false, //Hides or shows the infoWindow
      activeMarker: {}, //Shows the active marker upon click
      selectedPlace: {} //Shows the infoWindow to the selected place upon a marker
    };

    //event handler when marker is clicked
    //Used to show the google-maps-react component, InfoWindow
    //Visibility for InfoWindow is controlled by the boolean visible prop
    onMarkerClick = (props, marker, e) =>
      this.setState({
        selectedPlace: props,
        activeMarker: marker,
        showingInfoWindow: true
      });

    onClose = props => {
      if (this.state.showingInfoWindow) {
        this.setState({
          showingInfoWindow: false,
          activeMarker: null
        });
      }
    };

    return (
      //Map component takes in optional props
      <Map
        google={this.props.google}
        zoom={14}
        style={style} //takes in CSS mapStyles object
        initialCenter={{
          lat: -1.2884,
          lng: 36.823
        }}
      >
        <Marker onClick={this.onMarkerClick} name={"place"} />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow>
      </Map>
    );
  }
}

//Higher Order Component that provides wrapper around Google APIs
//Can be configured by passing a function that will be called with the wrapped component's props
export default GoogleApiWrapper({
  apiKey: "AIzaSyBFUHG2hhGRTX-FTfz3nwMMNPXKHvqGxZ8"
})(MapContainer);
