import React from "react";
import ReactDOM from "react-dom";
import { GoogleApiWrapper, InfoWindow, Marker } from "google-maps-react";
import Map from "../src/components/index.js";
import axios from "axios";

const API_KEY = `${process.env.REACT_APP_API_KEY}`;

export class MapContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      showingInfoWindow: false,
      activeMarker: {},
      selectedPlace: {},
      // an array of lngs and lats
      locations: [],
      names: [],
      hours: [],
      ratings: [],
      currentLocation: {
        lat: "",
        lng: ""
      }
    };
    this.handleLocationChange = this.handleLocationChange.bind(this);
    this.getMapInfo = this.getMapInfo.bind(this);
  }

  getMapInfo() {
    axios
      .get(
        `${"https://cors-anywhere.herokuapp.com/"}https://api.yelp.com/v3/businesses/search?`,
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_API_KEY}`
          },
          params: {
            categories: "coffee, Coffee & Tea, libraries",
            latitude: this.state.currentLocation.lat,
            longitude: this.state.currentLocation.lng
          }
        }
      )
      // set state for locations, names
      .then(res => {
        // this.setState({
        //  locations: this.state.locations.concat(res.business[0])
        // });
        for (var key in res.data.businesses) {
          this.setState({
            locations: this.state.locations.concat(
              res.data.businesses[key].coordinates
            ),
            names: this.state.names.concat(res.data.businesses[key].name),
            ratings: this.state.ratings.concat(res.data.businesses[key].rating)
          });
        }
        //for (var key in res.data.businesses) {
        // console.log(res.data.businesses[key].coordinates);
        //}
      })
      .catch(err => {
        console.log("Yelp API call error");
      });
  }

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onMapClicked = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  //after a location change call after recall the api
  // using a setState callback (setState(updater, callback))
  handleLocationChange(lat, lng) {
    this.setState(
      {
        currentLocation: {
          lat: lat,
          lng: lng
        }
      },
      () => {
        this.getMapInfo();
      }
    );
  }
  /*
  displayMarkers = () => {
    return this.state.locations.map((store,index) => {
      return <Marker key={index} id={index} position={{
        lat:store.
      }}
       />
    })
  }
*/

  // if a user clicks redo search in this area, call set state
  // which will re render everything
  // how to create event listeners for a component
  render() {
    const names = this.state.names;
    const locations = this.state.locations;

    const ratings = this.state.ratings;
    var elements = [];
    for (var i = 0; i < names.length; i++) {
      elements.push(
        <Marker
          onClick={this.onMarkerClick}
          title={names[i]}
          name={names[i]}
          position={{
            lat: locations[i].latitude,
            lng: locations[i].longitude
          }}
        />
      );
    }
    // loaded prop is from the GoogleApiComponent
    return (
      //getting a reference to a google object
      <Map
        centerAroundCurrentLocation
        google={this.props.google}
        style={{ width: "100%", height: "100%", position: "relative" }}
        className={"map"}
        zoom={14}
        onClick={this.onMapClicked}
        onLocationChange={this.handleLocationChange}
      >
        {elements}
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
        >
          <div>
            <h1>{this.state.selectedPlace.name}</h1>
          </div>
        </InfoWindow>
      </Map>
    );
  }
}

//Higher Order Component that provides wrapper around Google APIs
//Can be configured by passing a function that will be called with the wrapped component's props
export default GoogleApiWrapper({
  apiKey: "AIzaSyBFUHG2hhGRTX-FTfz3nwMMNPXKHvqGxZ8"
})(MapContainer);
