import React from "react";
import ReactDOM from "react-dom";
import { GoogleApiWrapper, Map, InfoWindow, Marker } from "google-maps-react";
//import Map from "./Map";

export class MapContainer extends React.Component {
  state = {
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {}
  };

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onMapClicked = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };
  render() {
    // loaded prop is from the GoogleApiComponent
    return (
      //getting a reference to a google object
      <Map
        google={this.props.google}
        style={{ width: "100%", height: "100%", position: "relative" }}
        className={"map"}
        zoom={14}
        onClick={this.onMapClicked}
      >
        <Marker
          onClick={this.onMarkerClick}
          title={"The marker`s title will appear as a tooltip."}
          name={"SOMA"}
          position={{ lat: 37.778519, lng: -122.40564 }}
        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
        >
          <div>
            <h1>{this.state.selectedPlace.name}</h1>
          </div>
        </InfoWindow>
        <Marker
          name={"Dolores park"}
          position={{ lat: 37.759703, lng: -122.428093 }}
        />
        <Marker />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
        >
          <div>
            <h1>{this.state.selectedPlace.name}</h1>
          </div>
        </InfoWindow>
        <Marker
          name={"Your position"}
          position={{ lat: 37.762391, lng: -122.439192 }}
          icon={{
            url: "/path/to/custom_icon.png"
          }}
        />
      </Map>
    );
  }
}

//Higher Order Component that provides wrapper around Google APIs
//Can be configured by passing a function that will be called with the wrapped component's props
export default GoogleApiWrapper({
  apiKey: "AIzaSyBFUHG2hhGRTX-FTfz3nwMMNPXKHvqGxZ8"
})(MapContainer);

