import React, { Component } from "react";
import ReactDOM from "react-dom";
import { GoogleApiWrapper, InfoWindow, Marker } from "google-maps-react";
import MapContainer from "./components/MapContainer";
import axios from "axios";

export class Container extends React.Component {
  constructor(props) {
    //state from map needs to live up here
    super(props);

    this.state = {
      showingInfoWindow: false,
      activeMarker: {},
      selectedPlace: {},
      addresses: [],
      locations: [],
      names: [],
      hours: [],
      ratings: [],
      currentLocation: {
        lat: "",
        lng: ""
      }
    };

    this.onMarkerClick = this.onMarkerClick.bind(this);
    this.handleOnMapClicked = this.handleOnMapClicked.bind(this);
    this.handleCenterMoved = this.handleCenterMoved.bind(this);
    this.handleLocationChange = this.handleLocationChange.bind(this);
  }

  onMarkerClick = (props, marker, e) => {
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });
  };

  handleOnMapClicked = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  handleCenterMoved(mapProps, map) {
    this.setState(
      {
        currentLocation: {
          lat: map.center.lat(),
          lng: map.center.lng()
        }
      },
      () => {
        this.getMapInfo();
      }
    );
  }

  handleLocationChange(lat, lng) {
    this.setState(
      {
        currentLocation: {
          lat: lat,
          lng: lng
        }
      },
      () => {
        this.getMapInfo();
      }
    );
  }

  getMapInfo() {
    axios
      .get(
        `${"https://cors-anywhere.herokuapp.com/"}https://api.yelp.com/v3/businesses/search?`,
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_API_KEY}`
          },
          params: {
            categories: "coffee, libraries",
            latitude: this.state.currentLocation.lat,
            longitude: this.state.currentLocation.lng,
            limit: 50
          }
        }
      )
      // set state for locations, names
      .then(res => {
        for (var key in res.data.businesses) {
          var addressesBase = res.data.businesses[key].location;
          this.setState({
            locations: this.state.locations.concat(
              res.data.businesses[key].coordinates
            ),
            names: this.state.names.concat(res.data.businesses[key].name),
            ratings: this.state.ratings.concat(res.data.businesses[key].rating),
            addresses: this.state.addresses.concat(
              "" +
                addressesBase.address1 +
                " " +
                addressesBase.city +
                "," +
                addressesBase.state +
                " " +
                addressesBase.zip_code
            )
          });
        }
      })
      .catch(err => {
        console.log("Yelp API call error");
      });
  }

  render() {
    const names = this.state.names;
    const locations = this.state.locations;
    const addresses = this.state.addresses;
    const ratings = this.state.ratings;
    const activeMarker = this.state.activeMarker;
    const showingInfoWindow = this.state.activeMarker;
    const selectedPlace = this.state.selectedPlace;
    return (
      <MapContainer
        names={names}
        locations={locations}
        addresses={addresses}
        ratings={ratings}
        activeMarker={activeMarker}
        showingInfoWindow={showingInfoWindow}
        selectedPlace={selectedPlace}
        onMapClicked={this.handleOnMapClicked}
        onMarkerClick={this.onMarkerClick}
        centerMoved={this.handleCenterMoved}
        handleLocationChange={this.handleLocationChange}
      />
    );
  }
}

export default Container;
