import React, { Component } from "react";
import ReactDOM from "react-dom";
import { render } from "@testing-library/react";
import "../App.css";
import ReviewModal from "./ReviewModal";
import WriteReviewModal from "./WriteReviewModal";

export class ViewList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      elements: []
    };
  }

  componentDidUpdate(prevProps) {
    if (this.props.names !== prevProps.names) {
      this.createList();
    }
  }

  componentDidMount() {
    this.createList();
  }
  logToConsole = (
    rateSeating,
    rateComfort,
    rateIspeed,
    rateNoise,
    rateOutlet
  ) => {
    console.log(rateSeating);
    console.log(rateComfort);
    console.log(rateIspeed);
    console.log(rateNoise);
    console.log(rateOutlet);
  };
  createList() {
    const names = this.props.names;
    const addresses = this.props.addresses;
    const ratings = this.props.ratings;
    const placeID = this.props.placeID;
    const newArray = [];
    for (var i = 0; i < names.length; i++) {
      newArray.push(
        <li>
          <div>{names[i]}</div>

          <div>{addresses[i]}</div>
          <div> Yelp rating: {ratings[i]}/5</div>
          <ReviewModal
            names={names[i]}
            ratings={ratings[i]}
            placeID={placeID[i]}
          />
          <br />
          <WriteReviewModal
            names={names[i]}
            logThis={this.logToConsole}
            placeID={placeID[i]}
          />
          <br />
        </li>
      );
    }
    this.setState({ elements: newArray });
  }

  render() {
    return (
      <div className={this.props.className} id={this.props.id}>
        <h1> Study Spots </h1>
        <ol>{this.state.elements}</ol>
      </div>
    );
  }
}
export default ViewList;
